mapper.select:
SELECT ROWNUM AS "ID",T.* FROM ( SELECT "NAME", AREA_NAME, POLICE_CATEGORY, TO_CHAR (CREATE_TIME,'yyyy-MM-dd HH:mm:ss') AS CREATE_TIME FROM TB_BIGDATA
${ew.sqlSegment}
) T

SELECT ROWNUM AS "ID",T.* FROM ( SELECT B.NAME AS serviceName, B.AREA_NAME AS serviceAreaName, B.POLICE_CATEGORY AS servicePoliceCategory, SUBSTR(I."ALIAS", 0, INSTR(I."ALIAS",'（')-1) AS appName, I.AREA_NAME AS appAreaName, I.POLICE_CATEGORY AS appPoliceCategory, TO_CHAR (I.CREATED_AT,'yyyy-MM-dd HH:mm:ss') AS CREATE_TIME FROM TB_SERVICE_INSTANCE I LEFT JOIN TB_BIGDATA B ON I.SERVICE_GUID = B.APIG_GUID
${ew.sqlSegment}
) T

SELECT ROWNUM AS "ID",T.* FROM ( SELECT B.NAME AS serviceName, B.AREA_NAME AS serviceAreaName, B.POLICE_CATEGORY AS servicePoliceCategory, SUBSTR(I."ALIAS", 0, INSTR(I."ALIAS",'（')-1) AS appName, I.AREA_NAME AS appAreaName, I.POLICE_CATEGORY AS appPoliceCategory, SUM(A.REQ_COUNT) AS totalReqCount FROM TB_APP_STATISTICS A LEFT JOIN TB_BIGDATA B ON A.PRODUCT_FAMILY_ID = B.APIPRODUCT_ID LEFT JOIN TB_SERVICE_INSTANCE I ON A.ACTUAL_ID = I.ACTUAL_ID AND I.SERVICE_GUID=B.APIG_GUID
${ew.sqlSegment}
) T

SELECT ROWNUM AS "ID",T.* FROM ( SELECT B.NAME AS serviceName, B.AREA_NAME AS serviceAreaName, B.POLICE_CATEGORY AS servicePoliceCategory, SUM(A .REQ_COUNT) AS reqCount, SUM((A .REQ_COUNT_4XX + A .REQ_COUNT_5XX)) AS abnormal, SUM(A .AVG_LATENCY) AS avgLatency, SUM(A .AVG_BACKEND_LATENCY) AS avgBackendLatency, COUNT(A.ID) AS num FROM TB_APP_STATISTICS A LEFT JOIN TB_BIGDATA B ON A .PRODUCT_FAMILY_ID = B.APIPRODUCT_ID
${ew.sqlSegment}
) T
