mapper.select:
select
  fs.*
from TB_FLOW_STEP fs


        right join TB_APPLY_FLOW af on af.ID = fs.FLOW_ID
        left join TB_FLOW_STEP_USER fsu on fs.ID = fsu.STEP_ID
        where
        fsu.USER_ID = #{user.idcard}
        and af.TARGET_RESOURCE = #{resourceType}


        left join TB_FLOW_STEP_USER fsu on fs.ID = fsu.STEP_ID
        left join TB_APPLICATION_INFO ai on fs.FLOW_ID = ai.SERVICE_TYPE_ID
        where
        fsu.USER_ID = #{user.idcard}
        and ai.RESOURCE_TYPE = #{resourceType}



    and fs.TYPE = #{stepType}

select
  fs.*
from TB_FLOW_STEP fs
left join TB_FLOW_STEP_USER fsu on fs.ID = fsu.STEP_ID
where
  fsu.USER_ID = #{user.idcard}
  and fs.FLOW_ID = #{serviceId}

    and fs.TYPE = #{stepType}
