mapper.select:
SELECT CPU_TOTAL,CPU_USAGE,CPU_USED,MEMORY_TOTAL,MEMORY_USAGE,MEMORY_USED FROM TB_PAAS_ZHZY
WHERE TYPE_SITE='hadoop'

        AND AREA=#{area}


        AND POLICE=#{police}

SELECT NVL(SUM(ELASTICSEARCH_CPU_TOTAL),0) ELASTICSEARCH_CPU_TOTAL, NVL(SUM(ELASTICSEARCH_MEMORY_TOTAL),0) ELASTICSEARCH_MEMORY_TOTAL, NVL(SUM(ELASTICSEARCH_MEMORY_USED),0) ELASTICSEARCH_MEMORY_USED, NVL(SUM(ELASTICSEARCH_DISK_AVAILABLE),0) ELASTICSEARCH_DISK_AVAILABLE, NVL(SUM(ELASTICSEARCH_DISK_USED),0) ELASTICSEARCH_DISK_USED, CASE NVL(SUM(ELASTICSEARCH_CPU_TOTAL),0) WHEN 0 THEN 0 ELSE ROUND( SUM( ELASTICSEARCH_CPU_TOTAL * ELASTICSEARCH_CPU_USAGE ) / SUM( ELASTICSEARCH_CPU_TOTAL ), 2 ) END ELASTICSEARCH_CPU_USAGE FROM TB_PAAS_ZHZY
AREA=#{area}
AND POLICE=#{police}

SELECT NVL(SUM(REDIS_MEMORY_TOTAL),0) REDIS_MEMORY_TOTAL, CASE NVL(SUM(REDIS_MEMORY_TOTAL),0) WHEN 0 THEN 0 ELSE ROUND( SUM( REDIS_MEMORY_TOTAL * REDIS_MEMORY_USAGE ) / SUM( REDIS_MEMORY_TOTAL ), 2 ) END REDIS_MEMORY_USAGE FROM TB_PAAS_ZHZY
AREA=#{area}
AND POLICE=#{police}

SELECT CPU_TOTAL,CPU_USAGE,CPU_USED,MEMORY_TOTAL,MEMORY_USAGE,MEMORY_USED FROM TB_PAAS_ZHZY
WHERE TYPE_SITE='Libra'

        AND AREA=#{area}


        AND POLICE=#{police}

SELECT APPLY_NAME FROM TB_PAAS_ZHZY


        AREA=#{area}


        AND POLICE=#{police}

SELECT SUM(CPU_TOTAL) CPU_TOTAL,SUM(MEMORY_TOTAL) MEMORY_TOTAL,SUM(STORAGE_TOTAL) STORAGE_TOTAL FROM TB_PAAS_ZHZY
WHERE TYPE_SITE='hadoop' AND ODS_TIME=(SELECT MAX(ODS_TIME) FROM TB_PAAS_ZHZY)

    AND AREA=#{area}


    AND POLICE=#{police}

SELECT SUPER_CLUSTER_CN,SUPER_CLUSTER_EN,NVL(ELASTICSEARCH_HOSTS_NUMBER,0) ELASTICSEARCH_HOSTS_NUMBER FROM TB_PAAS_ZHZY
WHERE ODS_TIME=(SELECT MAX(ODS_TIME) FROM TB_PAAS_ZHZY)

    AND AREA=#{area}


    AND POLICE=#{police}

SELECT SUM(REDIS_MEMORY_TOTAL) REDIS_MEMORY_TOTAL FROM TB_PAAS_ZHZY
WHERE ODS_TIME=(SELECT MAX(ODS_TIME) FROM TB_PAAS_ZHZY)

    AND AREA=#{area}


    AND POLICE=#{police}

SELECT SUM(CPU_TOTAL) CPU_TOTAL,SUM(MEMORY_TOTAL) MEMORY_TOTAL,SUM(STORAGE_TOTAL) STORAGE_TOTAL FROM TB_PAAS_ZHZY
WHERE TYPE_SITE='Libra' AND ODS_TIME=(SELECT MAX(ODS_TIME) FROM TB_PAAS_ZHZY)

    AND AREA=#{area}


    AND POLICE=#{police}

SELECT CPU_TOTAL,CPU_USAGE,CPU_USED,MEMORY_TOTAL,MEMORY_USAGE,MEMORY_USED,ODS_TIME FROM TB_PAAS_ZHZY
WHERE TYPE_SITE='hadoop'  and APPLY_NAME=#{appName}

order by ODS_TIME

SELECT CPU_TOTAL,CPU_USAGE,CPU_USED,MEMORY_TOTAL,MEMORY_USAGE,MEMORY_USED,ODS_TIME,STORAGE_TOTAL FROM TB_PAAS_ZHZY
WHERE TYPE_SITE='Libra'  and APPLY_NAME=#{appName}

order by ODS_TIME

SELECT REDIS_MEMORY_USAGE,ODS_TIME FROM TB_PAAS_ZHZY
WHERE    APPLY_NAME=#{appName}

order by ODS_TIME
