mapper.select:
SELECT b.SERVICE_NAME SERVICE_NAME,count(1) OPENING_NUMBER,b.MODIFIED_TIME OPENING_TIME FROM TB_SAAS_APPLICATION a JOIN TB_SAAS_APPLICATION_EXT b ON a.ID = b.MASTER_ID WHERE a.RECOVER_FLAG != '-1' and a.STATUS ='3' and not exists (select ra.CREATOR from TB_SAAS_RECOVER_APPLICATION ra,TB_SAAS_RECOVER_INFO ri where ri.APP_INFO_ID = ra.ID and ra.STATUS != '3' and ri.RE_IDCARD = a.CREATOR)
and a.CREATOR=#{creator}
and b.SERVICE_NAME=#{serviceName}
GROUP BY b.SERVICE_NAME,b.MODIFIED_TIME

SELECT b.SERVICE_NAME SERVICE_NAME,count(1) RECYCLING_NUMBER,b.MODIFIED_TIME RECYCLING_TIME FROM TB_SAAS_APPLICATION a JOIN TB_SAAS_APPLICATION_EXT b ON a.ID = b.MASTER_ID WHERE a.RECOVER_FLAG = '-1' and a.STATUS ='3' and not exists (select ra.CREATOR from TB_SAAS_RECOVER_APPLICATION ra,TB_SAAS_RECOVER_INFO ri where ri.APP_INFO_ID = ra.ID and ra.STATUS != '3' and ri.RE_IDCARD = a.CREATOR)
and a.CREATOR=#{creator}
and b.SERVICE_NAME=#{serviceName}
GROUP BY b.SERVICE_NAME,b.MODIFIED_TIME
