    and  a.AREA = #{p.queryVO.area}
    and  a.AREA != '省厅'


    and a.CREATOR = #{user.idcard}

select count(*) from TB_SAAS_REGISTER a where a.STATUS = 3

    and  a.POLICE_CATEGORY like #{p.queryVO.policeCategory}
